import { useEffect, useState } from 'react'
import { Container, CartTitle, List, TotalContainer, ButtonContainer} from './styles';
import { api } from '../../services/api';

interface Item{

  id: number,
  imageSource: string,
  name: string,
  price: number,
  listPrice: number
}

export function Cart() {

  const [itens, setItens] = useState([]);
  const [total, setTotal] = useState(0);

  useEffect(() => {
    api('itens')
    .then( response => {
      setItens(response.data);
      setTotal(itens.reduce((total, item:Item) => {
        return total + item.price;
      }, 0));
    });
  }, [])

  return (
    <Container>
      <CartTitle>Meu carrinho</CartTitle>
      <List>
        {itens.map( (item:Item) => {
          return (
            <li key={item.id}>
              <img src={item.imageSource} alt="item"/>
              <div>
                <h4>{item.name}</h4>
                <span>R$ {item.price}</span>
                <strong>R$ {item.listPrice}</strong>
              </div>
            </li>
          )
        })} 
      </List>
      <TotalContainer>
        <div>
          <strong>Total</strong>
          <strong>R$ {total}</strong>
        </div>
        <p>Parabéns, sua compra tem frete grátis !</p>
      </TotalContainer>
      <ButtonContainer>
        <button>
          Finalizar compra
        </button>
      </ButtonContainer>
    </Container>
  )
}
